func:
switch(msg.topic){
    case "AirHum":
        //value to be print
        deviceId=global.get("deviceID");
        devicelength=deviceId.length;
        //when the element is only one
        if(devicelength==1&&msg.payload.i==deviceId[0]){
            value=msg.payload.v;
            msg.payload=value;
            msg.topic="AirHum1";
        }
        //when the device are two split into the first or second graph
        else if(devicelength!=1&&msg.payload.i==deviceId[0]){
            value=msg.payload.v;
            msg.payload=value;
            msg.topic="AirHum1";
        }
        else if(devicelength!=1 && msg.payload.i==deviceId[1]){
            value=msg.payload.v;
            msg.payload=value;
            msg.topic="AirHum2";
        }
        else{
            msg.topic="none";
        }
        msg.check=deviceId;
        break;

    case "choiceID":
        //possible only two choice
        if(msg.payload.length>2){
            tmp=[msg.payload[0],msg.payload[1]];
            msg.payload=tmp;
        }
        devicesId=global.get("deviceID");
        //consider all the options
        //when there is no device in the global 
        if(devicesId.length==0){
            global.set("deviceID",msg.payload);
            //make a reset
            msg["payload"]=[];
            msg["topic"]="both";
            msg["check"]=global.get("deviceID");
        }
        //only one choice and only one device different from the previous one
        else if(msg.payload.length==1&&devicesId.length==1&&msg.payload!= devicesId){
            global.set("deviceID",msg.payload);
            msg["payload"]=[];
            msg["topic"]="AirHum1";
        }
        //only one choice and only one device equal to the previous one
        else if(msg.payload.length==1&&devicesId.length==1&&msg.payload==devicesId){
            msg.topic="none";
        }
        //only one choice in two devices
        else if(msg.payload.length==1&&devicesId.length!=1)
        {
                global.set("deviceID",msg.payload);
                //if the id is the same of the first chart second one reset
                if(msg.payload==devicesId[0]){
                    msg["payload"]=[];
                    msg["topic"]="AirHum2";
                }
                else{
                    msg["payload"]=[];
                    msg["topic"]="both";
                }
                //otherwise both reset

        }
        //two choice and one element in devices
        else if(msg.payload.length!=1&&devicesId.length==1){
            //if the element is present in one of the two choice
            //important the way the two element is saved into the global variable
            if(msg.payload[0]==devicesId[0]){
                global.set("deviceID",msg.payload);
                //reset the second graph the first remain invariato
                msg["payload"]=[];
                msg["topic"]="AirHum2";
            }
            else if(msg.payload[1]==devicesId[0]){
                tmp=[msg.payload[1],msg.payload[0]];
                global.set("deviceID",tmp);
                //reset the second graph
                msg["payload"]=[];
                msg["topic"]="AirHum2";
            }
            //it is not present nether in one or two
            else{
                //reset the graphs
                global.set("deviceID",msg.payload);
                msg["payload"]=[];
                msg["topic"]="both";
            }
        }
        //two choice and two elements in devices
        else if(msg.payload.length!=1&&devicesId.length!=1){
            //when the two element are equal
            if(msg.payload[0]==devicesId[0]&&msg.payload[1]==devicesId[1]){
                msg.topic="none";
                //nothing change
            }
            //when the first element change
            else if(msg.payload[0]!=devicesId[0]&&msg.payload[1]==devicesId[1]){
                global.set("deviceID",msg.payload);
                msg["payload"]=[];
                msg["topic"]="AirHum1";
            }
            //when the second element change
            else if(msg.payload[1]!=deviceId[1]&&msg.payload[0]==deviceId[0]){
                global.set("deviceID",msg.payload);
                msg["payload"]=[];
                msg["topic"]="AirHum2";
            }
            //when both change
            else{
                global.set("deviceID",msg.payload);
                msg.topic="both"
                msg["payload"]=[];
            }
        }
        break;
}
return msg;

